import React from 'react';
import Form from '../form/form'

const FocusVisible = styled.form`
&.js-focus-visible :focus:not(.focus-visible) {
   outline: none;
}
&.js-focus-visible .focus-visible {
   outline: none;
   border: 3px solid #528deb;
}
`;

export default FocusVisible;
