import React, { Component } from 'react';
import './form.css';
import axios from 'axios';

const utilizeFocus = () => {
	const ref = React.createRef()
	const setFocus = () => {ref.current &&  ref.current.focus()}

	return {setFocus, ref} 
}

class Form extends Component {
	constructor(props){
		super (props);
		this.state = {
			name: '',
			email: '',
			message: '',
		}
	this.input1Focus = utilizeFocus()
    this.input2Focus = utilizeFocus()
 	}

 	onNameChange(event) {
 		this.setState({name: event.target.value})

 	}

 	onEmailChange(event){
 		this.setState({email: event.target.value})
 	}

 	onMessageChange(event){
 		this.setState({message: event.target.value})
 	}

 	resetForm(){
 		this.setState({name:'', email:'', message: ''})
 	}
 	 componentDidMount(){
    this.input1Focus.setFocus()
  }

 	handleSubmit(event){
 	event.preventDefault();
 		axios({
 			method: "POST",
 			url: "http://localhost:3002/send",
 			data: this.state
 		}).then((response) => {
 			if(response.data.status === 'success'){
 				alert('Message sent');
 				this.resetForm()
 			} else if(response.data.status === 'fail') {
 				alert('Message failed to send')
 			}
 		})
 	}

render(){
	return (
	<div className="form-box">
	 <form method="POST" onSubmit={this.handleSubmit.bind(this)}> 
	  <div className="user-box">
	     <label>Name</label>
	    <input onChange={(e)=>{
              if (e.target.value.length>=1) this.input2Focus.setFocus()
            }}

            ref={this.input1Focus.ref} type="text" required="" value={this.state.name} onChange={this.onNameChange.bind(this)} />
	  </div>
	  <div className="user-box">
	      <label>Email address</label>
	      <input ref={this.secondInput} type="email" className="form-control" aria-describedby="emailHelp" value={this.state.email} onChange={this.onEmailChange.bind(this)}/>
	  </div>
	  <div className="user-box">

	  	   <textarea  autoFocus placeholder="The personality" className="user-box" rows="5" value={this.state.message} onChange={this.onMessageChange.bind(this)} />
	  </div>
	       <button type="submit" className="btn-submit">Submit</button>
	 </form>
	 </div>
	)
 }
}
export default Form;
